import { Component, ElementRef, ViewChild, AfterViewInit, OnInit } from '@angular/core';
import { ApiserviceService } from 'src/app/apiservice.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit, AfterViewInit {
  extypeData: any;
  selectedExType: string = '';
  inctypeData: any;
  selectedIncType: string = '';

  constructor(private service: ApiserviceService) { }

  ngOnInit(): void {
    this.service.getAllExType().subscribe(response => {
      this.extypeData = response.data;
    })
    this.service.getAllIncType().subscribe(response => {
      this.inctypeData = response.data;
    })
  }

  @ViewChild('currencyInput', { static: false }) currencyInput!: ElementRef;

  ngAfterViewInit(): void {
    this.currencyInput.nativeElement.addEventListener('keyup', () => {
      this.formatCurrency(this.currencyInput.nativeElement);
    });
    this.currencyInput.nativeElement.addEventListener('blur', () => {
      this.formatCurrency(this.currencyInput.nativeElement, 'blur');
    });
  }

  formatNumber(n: string): string {
    // format number 1000000 to 1,234,567
    return n.replace(/\D/g, '').replace(/\B(?=(\d{3})+(?!\d))/g, ',');
  }

  formatCurrency(input: HTMLInputElement, blur: string = ''): void {
    // appends $ to value, validates decimal side
    // and puts the cursor back in the right position.

    // get input value
    const inputVal = input.value;

    // don't validate empty input
    if (inputVal === '') {
      return;
    }

    // original length
    const originalLen = inputVal.length;

    // initial caret position
    const caretPos = input.selectionStart?.valueOf() || 0; 

    // check for decimal
    if (inputVal.indexOf('.') >= 0) {
      // get the position of the first decimal
      const decimalPos = inputVal.indexOf('.');

      // split number by decimal point
      const leftSide = inputVal.substring(0, decimalPos);
      const rightSide = inputVal.substring(decimalPos);

      // add commas to the left side of the number
      const formattedLeftSide = this.formatNumber(leftSide);

      // validate the right side
      let formattedRightSide = this.formatNumber(rightSide);

      // On blur, make sure there are 2 numbers after the decimal
      if (blur === 'blur') {
        formattedRightSide += '00';
      }

      // Limit the decimal to only 2 digits
      const limitedRightSide = formattedRightSide.substring(0, 2);

      // join the number by .
      input.value = `${formattedLeftSide}.${limitedRightSide}`;
    } else {
      // no decimal entered
      // add commas to the number
      // remove all non-digits
      let formattedInputVal = this.formatNumber(inputVal);
      formattedInputVal = `${formattedInputVal}`;

      // final formatting
      if (blur === 'blur') {
        formattedInputVal += '.00';
      }

      input.value = formattedInputVal;
    }

    // put the caret back in the right position
    const updatedLen = input.value.length;
    const updatedCaretPos = updatedLen - originalLen + caretPos;
    input.setSelectionRange(updatedCaretPos, updatedCaretPos);
  }
}
