import { Component, OnInit } from '@angular/core';
import { ApiserviceService } from 'src/app/apiservice.service';
import { FormGroup, FormBuilder } from '@angular/forms';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  extypeData: any[] = [];
  selectedExType: string = '';
  inputExAmount: string = '';

  inctypeData: any[] = [];
  selectedIncType: string = '';
  inputIncAmount: string = '';

  exData: any[] = [];
  extypeSingleData: any[] = [];

  expenseForm: FormGroup;
  incomeForm: FormGroup;
  select: any;
  incData: any[] = [];

  constructor(
    private service: ApiserviceService,
    private fb: FormBuilder,
  ) {
    this.expenseForm = this.fb.group({
      inputExtypeID: [''],
      inputExAmount: ['']
    })

    this.incomeForm = this.fb.group({
      inputInctypeID: [''],
      inputIncAmount: ['']
    })
  }

  ngOnInit(): void {
    this.service.getAllExType().subscribe(response => {
      this.extypeData = response.data;
    })
    this.service.getAllIncType().subscribe(response => {
      this.inctypeData = response.data;
    })

    this.service.getAllEx().subscribe(response => {
      this.exData = response.data;
    })
    this.service.getAllInc().subscribe(response => {
      this.incData = response.data;
    })

  }

  getExtypeName(extypeID: number): string {
    const extype = this.extypeData.find(item => item.extypeID === extypeID);
    return extype ? extype.extypeName : '';
  }
  getInctypeName(inctypeID: number): string {
    const inctype = this.inctypeData.find(item => item.inctypeID === inctypeID);
    return inctype ? inctype.inctypeName : '';
  }

  addExData() {
    const extypeID = this.expenseForm.value.inputExtypeID;
    const exAmount = this.expenseForm.value.inputExAmount;

    this.service.createEx(extypeID, exAmount).subscribe(() => {
      this.expenseForm.reset();
      window.location.reload();
    })
  }

  addIncData() {
    const inctypeID = this.incomeForm.value.inputInctypeID;
    const incAmount = this.incomeForm.value.inputIncAmount;

    this.service.createInc(inctypeID, incAmount).subscribe(() => {
      this.incomeForm.reset();
      window.location.reload();
    })
  }

  deleteAllExpenses() {
    Swal.fire({
      title: 'ต้องการลบรายการรายจ่ายทั้งหมด ?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'แน่นอน',
      cancelButtonText: 'ยกเลิก',
    }).then((result) => {
      if (result.isConfirmed) {
        // Delete all expenses
        this.service.deleteAllEx().subscribe(() => {
          window.location.reload();
        });
      }
    });
  }

  deleteAllIncome() {
    Swal.fire({
      title: 'ต้องการลบรายการรายจ่ายทั้งหมด ?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'แน่นอน',
      cancelButtonText: 'ยกเลิก',
    }).then((result) => {
      if (result.isConfirmed) {
        // Delete all income
        this.service.deleteAllInc().subscribe(() => {
          window.location.reload();
        });
      }
    });
  }

  deleteSingleExpenses(i: number) {
    Swal.fire({
      title: 'ต้องการลบรายจ่ายแถวนี้ ?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'แน่นอน',
      cancelButtonText: 'ยกเลิก',
    }).then((result) => {
      if (result.isConfirmed) {
        this.service.deleteEx(this.exData[i].exID).subscribe(() => {
          this.exData.splice(i, 1);
        });
      }
    });
  }

  deleteSingleIncome(i: number) {
    Swal.fire({
      title: 'ต้องการลบรายจ่ายแถวนี้ ?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonText: 'แน่นอน',
      cancelButtonText: 'ยกเลิก',
    }).then((result) => {
      if (result.isConfirmed) {
        this.service.deleteInc(this.incData[i].incID).subscribe(() => {
          this.incData.splice(i, 1);
        });
      }
    });
  }

  editSingleExpenses(i: number) {
    const exData = this.exData[i]

    Swal.fire({
      title: `แก้ไขรายการรายจ่าย`,
      html: `
        <div class="grid grid-cols-4 place-items-center bg-orange-400 rounded-t text-stone-800 text-xl font-medium">
          <div class="bg-white py-1 w-full h-full"></div>
          <h3>ชนิด</h3>
          <h3>ราคา</h3>
          <h3>ราคา</h3>
        </div>
        <div class="grid grid-cols-4 place-items-center bg-stone-800 text-white overflow-hidden">
          <div class="w-full h-full bg-red-400 py-1">
            <p>เก่า</p>
          </div>
          <p>${this.getExtypeName(exData.extypeID)}</p>
          <p>${exData.exAmount}</p>
          <p>${exData.exDate}</p>
        </div>
        <div class="mt-16">
          <form class="grid grid-cols-[40%_40%] gap-4 w-full place-content-center" id="formEEF">
            <select id="inputEEID" name="extype" class="px-2 rounded bg-stone-800 text-white">
              <option value="" disabled selected>-- ชนิด --</option>
            </select>
            <div class="relative w-full">
              <span class="absolute ml-2 left-0 h-full flex justify-center items-center text-orange-400">฿</span>
              <input id="inputEEA" class="w-full h-full py-1 pl-6 rounded focus:outline-none bg-stone-800 text-white" placeholder="1000.00"/>
            </div>
          </form>
        </div>
      `,
      confirmButtonColor: 'rgb(251 146 60)',
      confirmButtonText: 'บันทึก',
      showCancelButton: true,
      cancelButtonText: 'ยกเลิก',
      preConfirm: () => {
        const extypeIDEdit = document.querySelector('#inputEEID') as HTMLSelectElement;
        const exAmountEdit = document.querySelector('#inputEEA') as HTMLSelectElement;
        const exID = this.exData[i].exID;

        if (!extypeIDEdit || !exAmountEdit || !exAmountEdit.value) {
          Swal.fire({
            title: "แก้ไขไม่ได้!",
            text: "กรุณากรอกข้อมูลให้ครบ",
            icon: "error",
            confirmButtonText: "รับทราบ",
            confirmButtonColor: 'rgb(251 146 60)'
          });
          return;
        }

        this.service.updateEx(extypeIDEdit.value, exAmountEdit.value, exID).subscribe(() => {
          window.location.reload();
        })
      }
    })

    const inputEEID = document.querySelector('#inputEEID') as HTMLSelectElement;

    if (inputEEID) {
      this.extypeData.forEach((item) => {
        const option = document.createElement('option')
        option.value = item.extypeID
        option.text = item.extypeName
        inputEEID.appendChild(option)
      })
    }

  }

  editSingleIncome(i: number) {
    const incData = this.incData[i]

    Swal.fire({
      title: `แก้ไขรายการรายรับ`,
      html: `
        <div class="grid grid-cols-4 place-items-center bg-orange-400 rounded-t text-stone-800 text-xl font-medium">
          <div class="bg-white py-1 w-full h-full"></div>
          <h3>ชนิด</h3>
          <h3>ราคา</h3>
          <h3>ราคา</h3>
        </div>
        <div class="grid grid-cols-4 place-items-center bg-stone-800 text-white overflow-hidden">
          <div class="w-full h-full bg-red-400 py-1">
            <p>เก่า</p>
          </div>
          <p>${this.getInctypeName(incData.inctypeID)}</p>
          <p>${incData.incAmount}</p>
          <p>${incData.incDate}</p>
        </div>
        <div class="mt-16">
          <form class="grid grid-cols-[40%_40%] gap-4 w-full place-content-center" id="formInEF">
            <select id="inputInEID" name="inctype" class="px-2 rounded bg-stone-800 text-white">
              <option value="" disabled selected>-- ชนิด --</option>
            </select>
            <div class="relative w-full">
              <span class="absolute ml-2 left-0 h-full flex justify-center items-center text-orange-400">฿</span>
              <input id="inputInEA" class="w-full h-full py-1 pl-6 rounded focus:outline-none bg-stone-800 text-white" placeholder="1000.00"/>
            </div>
          </form>
        </div>
      `,
      confirmButtonColor: 'rgb(251 146 60)',
      confirmButtonText: 'บันทึก',
      showCancelButton: true,
      cancelButtonText: 'ยกเลิก',
      preConfirm: () => {
        const inctypeIDEdit = document.querySelector('#inputInEID') as HTMLSelectElement;
        const incAmountEdit = document.querySelector('#inputInEA') as HTMLSelectElement;
        const incID = this.incData[i].incID;

        if (!inctypeIDEdit || !incAmountEdit || !incAmountEdit.value) {
          Swal.fire({
            title: "แก้ไขไม่ได้!",
            text: "กรุณากรอกข้อมูลให้ครบ",
            icon: "error",
            confirmButtonText: "รับทราบ",
            confirmButtonColor: 'rgb(251 146 60)'
          });
          return;
        }

        this.service.updateInc(inctypeIDEdit.value, incAmountEdit.value, incID).subscribe(() => {
          window.location.reload();
        })
      }
    })

    const inputInEID = document.querySelector('#inputInEID') as HTMLSelectElement;

    if (inputInEID) {
      this.inctypeData.forEach((item) => {
        const option = document.createElement('option')
        option.value = item.inctypeID
        option.text = item.inctypeName
        inputInEID.appendChild(option)
      })
    }

  }
}

<div class="text-stone-800 grid grid-cols-1 place-items-center place-content-start min-h-screen mt-16">

    <div class="outline w-3/4 py-2 rounded">
        <div></div>
        <div></div>
        <div></div>
        <div></div>
    </div>



    <div class="grid grid-cols-2 py-8 w-3/4 gap-6 max-lg:grid-cols-1">


        <!--------------------- INCOME FORM --------------------->
        <div class="flex flex-col gap-4">
            <div class="flex flex-col">
                <div class="rounded-t-lg  w-1/2 pl-3 py-1 bg-orange-400">
                    <h1 class="text-xl font-medium"><i class="bi bi-piggy-bank-fill text-white mr-1"></i> รายรับ</h1>
                </div>
                <div class="rounded-b-lg rounded-tr-lg p-2 bg-stone-800">
                    <div>
                        <form class="flex justify-between gap-2" [formGroup]="incomeForm" (submit)="addIncData()">
                            <select formControlName="inputInctypeID" name="inctype" class="px-2 rounded">
                                <option value="" disabled selected>-- ชนิด --</option>
                                <option *ngFor="let itemInc of inctypeData" [value]="itemInc.inctypeID">{{ itemInc.inctypeName}}</option>
                            </select>
                            <div class="relative w-full">
                                <span class="absolute ml-2 left-0 h-full flex justify-center items-center text-orange-400">฿</span>
                                <input formControlName="inputIncAmount" class="w-full h-full py-1 pl-6 rounded focus:outline-none" placeholder="1000.00"/>
                            </div>
                            <button class="text-white bg-orange-400 px-4 rounded transition-colors hover:bg-orange-600">เพิ่ม</button>
                        </form>
                    </div>
                    <div class="mt-4">
                        <div class="flex justify-between">
                            <h2 class="text-white text-2xl">รายการทั้งหมด</h2>
                            <button (click)="deleteAllIncome()" class="bg-white px-2 rounded transition-colors hover:bg-red-400 hover:text-white"><i class="bi bi-calendar-x mr-2" ></i>ลบรายการทั้งหมด</button>
                        </div>
                        <div class="mt-2">
                            <div class="grid grid-cols-4 w-full place-items-center font-medium text-xl rounded-t-lg bg-orange-400 overflow-hidden">
                                <h3>ชนิด</h3>
                                <h3>ราคา</h3>
                                <h3>วันที่</h3>
                                <div class="bg-stone-700 w-full grid place-items-center text-orange-400">
                                    <h3>จัดการ</h3>
                                </div>
                            </div>
                            <div class="rounded-b-lg bg-white divide-y-2 overflow-hidden">
                                <div *ngIf="incData && incData.length > 0">
                                    <div *ngFor="let item of incData; index as i" class="grid grid-cols-4 w-full place-items-center">
                                        <p>{{ getInctypeName(item.inctypeID) }}</p>
                                        <p>{{ item.incAmount }}</p>
                                        <p>{{ item.incDate }}</p>
                                        <div class="bg-stone-600 w-full h-full flex justify-center items-center gap-2 py-1">
                                            <button (click)="editSingleIncome(i)" title="แก้ไข" class="bg-white px-2 rounded transition-colors hover:bg-blue-400 hover:text-white"><i class="bi bi-pencil-square"></i></button>
                                            <button (click)="deleteSingleIncome(i)" title="ลบ" class="bg-white px-2 rounded transition-colors hover:bg-red-400 hover:text-white"><i class="bi bi-x"></i></button>
                                        </div>
                                    </div>
                                </div>
                                <div *ngIf="incData && incData.length === 0" class="flex justify-center items-center py-2">
                                    <p>-- ข้อมูลว่าง --</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>


        <!--------------------- EXPENSES FORM --------------------->
        <div class="flex flex-col gap-4">
            <div class="flex flex-col">
                <div class="rounded-t-lg  w-1/2 pl-3 py-1 bg-orange-400">
                    <h1 class="text-xl font-medium"><i class="bi bi-currency-exchange text-white mr-1"></i> รายจ่าย</h1>
                </div>
                <div class="rounded-b-lg rounded-tr-lg p-2 bg-stone-800">
                    <div>
                        <form class="flex justify-between gap-2" [formGroup]="expenseForm" (submit)="addExData()">
                            <select formControlName="inputExtypeID" name="extype" class="px-2 rounded">
                                <option value="" disabled selected>-- ชนิด --</option>
                                <option *ngFor="let itemEx of extypeData" [value]="itemEx.extypeID">{{ itemEx.extypeName}}</option>
                            </select>
                            <div class="relative w-full">
                                <span class="absolute ml-2 left-0 h-full flex justify-center items-center text-orange-400">฿</span>
                                <input formControlName="inputExAmount" class="w-full h-full py-1 pl-6 rounded focus:outline-none" placeholder="1000.00"/>
                            </div>
                            <button class="text-white bg-orange-400 px-4 rounded transition-colors hover:bg-orange-600">เพิ่ม</button>
                        </form>
                    </div>
                    <div class="mt-4">
                        <div class="flex justify-between">
                            <h2 class="text-white text-2xl">รายการทั้งหมด</h2>
                            <button (click)="deleteAllExpenses()" class="bg-white px-2 rounded transition-colors hover:bg-red-400 hover:text-white"><i class="bi bi-calendar-x mr-2" ></i>ลบรายการทั้งหมด</button>
                        </div>
                        <div class="mt-2">
                            <div class="grid grid-cols-4 w-full place-items-center font-medium text-xl rounded-t-lg bg-orange-400 overflow-hidden">
                                <h3>ชนิด</h3>
                                <h3>ราคา</h3>
                                <h3>วันที่</h3>
                                <div class="bg-stone-700 w-full grid place-items-center text-orange-400">
                                    <h3>จัดการ</h3>
                                </div>
                            </div>
                            <div class="rounded-b-lg bg-white divide-y-2 overflow-hidden">
                                <div *ngIf="exData && exData.length > 0">
                                    <div *ngFor="let item of exData; index as i" class="grid grid-cols-4 w-full place-items-center">
                                        <p>{{ getExtypeName(item.extypeID) }}</p>
                                        <p>{{ item.exAmount }}</p>
                                        <p>{{ item.exDate }}</p>
                                        <div class="bg-stone-600 w-full h-full flex justify-center items-center gap-2 py-1">
                                            <button (click)="editSingleExpenses(i)" title="แก้ไข" class="bg-white px-2 rounded transition-colors hover:bg-blue-400 hover:text-white"><i class="bi bi-pencil-square"></i></button>
                                            <button (click)="deleteSingleExpenses(i)" title="ลบ" class="bg-white px-2 rounded transition-colors hover:bg-red-400 hover:text-white"><i class="bi bi-x"></i></button>
                                        </div>
                                    </div>
                                </div>
                                <div *ngIf="exData && exData.length === 0" class="flex justify-center items-center py-2">
                                    <p>-- ข้อมูลว่าง --</p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    </div>
</div>
