import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ApiserviceService } from 'src/app/apiservice.service';
import { FormGroup, FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-edit-expenses',
  templateUrl: './edit-expenses.component.html',
  styleUrls: ['./edit-expenses.component.css']
})
export class EditExpensesComponent implements OnInit {
  id: any;
  exData: any;
  expenseForm: FormGroup;

  extypeData: any[] = [];

  constructor(
    private activatedRoute: ActivatedRoute,
    private service: ApiserviceService,
    private fb: FormBuilder
  ) { 
    this.expenseForm = this.fb.group({
      extypeID: [''],
      exAmount: ['']
    })
  }

  ngOnInit(): void {
    this.id = this.activatedRoute.snapshot.params['id'];

    this.service.getAllExType().subscribe(response => {
      this.extypeData = response.data;
    })

    this.service.getSingleEx(this.id).subscribe(data => {
      this.exData = data

      this.expenseForm = this.fb.group({
        extypeID: [this.exData.extypID],
        exAmount: [this.exData.exAmount]
      })
    })
  }

  getExtypeName(extypeID: number): string {
    const extype= this.extypeData.find(item => item.extypeID === extypeID);
    return extype ? extype.extypeName : '';
  }

  updateExpense() {
    this.service.updateEx(this.expenseForm.value.extypeID, this.expenseForm.value.exAmount, this.id);
  }

}
