import { Component, OnInit } from '@angular/core';
import { ApiserviceService } from 'src/app/apiservice.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit{
  extypeData: any;
  selectedExType: string = '';
  inctypeData: any;
  selectedIncType: string = '';

  constructor(private service: ApiserviceService) { }

 ngOnInit(): void {
   this.service.getAllExType().subscribe(response => {
    this.extypeData = response.data;
   })
   this.service.getAllIncType().subscribe(response => {
    this.inctypeData = response.data;
   })
 }
}
<div class="text-stone-800 flex justify-center">
    <div class="grid grid-cols-2 py-8 w-3/4 gap-12 max-lg:grid-cols-1">
        <div class="flex flex-col gap-4">
            <div class="flex flex-col">
                <div class="rounded-t-lg  w-1/2 pl-3 py-1 bg-orange-400">
                    <h1 class="text-xl font-medium"><i class="bi bi-piggy-bank-fill text-white mr-1"></i> รายรับ</h1>
                </div>
                <div class="rounded-b-lg rounded-tr-lg p-2 bg-stone-800">
                    <div>
                        <form>
                            <select [(ngModel)]="selectedExType" name="extype" class="px-2 rounded border-0 ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:max-w-xs sm:text-sm sm:leading-6">
                                <option value="" disabled selected>-- เลือกประเภท --</option>
                                <option *ngFor="let item of extypeData" [value]="item.extypeID">{{ item.extypeName}}</option>
                            </select>
                        </form>
                    </div>
                </div>
            </div>
        </div>
        <div class="flex flex-col gap-4">
            <div class="flex flex-col">
                <div class="rounded-t-lg  w-1/2 pl-3 py-1 bg-orange-400">
                    <h1 class="text-xl font-medium"><i class="bi bi-currency-exchange text-white mr-1"></i> รายจ่าย</h1>
                </div>
                <div class="rounded-b-lg rounded-tr-lg p-2 bg-stone-800">
                    <div>
                        <form>
                            <select [(ngModel)]="selectedExType" name="extype" class="px-2">
                                <option value="" disabled selected>-- เลือกประเภท --</option>
                                <option *ngFor="let item of inctypeData" [value]="item.inctypeID">{{ item.inctypeName}}</option>
                            </select>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
